# Audit of the AD Integrated Administrator account
# Generates a CSV report with detected uses

# Integrated Administrator account recovery
$adminUser = Get-ADUser -Filter { SID -like '*-500' } -Properties Enabled, LastLogonDate

# Account status check
Write-Host "Audit du compte Administrateur intégré : $($adminUser.Name)" -ForegroundColor Cyan
Write-Host "Statut du compte : $($adminUser.Enabled)"
Write-Host "Dernière connexion : $($adminUser.LastLogonDate)`n"

# Search in scheduled tasks
$tasksReport = @()
$computers = Get-ADComputer -Filter * | Select-Object -ExpandProperty Name

foreach ($computer in $computers) {
    try {
        # Tâches planifiées
        $tasks = Invoke-Command -ComputerName $computer -ScriptBlock {
            Get-ScheduledTask | Where-Object {
                $_.Principal.UserId -eq "Administrator" -or 
                $_.Principal.UserId -eq "$($using:adminUser.SamAccountName)" -or
                $_.Principal.UserId -like "*\$($using:adminUser.SamAccountName)"
            } | Select-Object TaskName, Principal
        } -ErrorAction Stop

        foreach ($task in $tasks) {
            $tasksReport += [PSCustomObject]@{
                Computer    = $computer
                Type        = "Tâche planifiée"
                Nom         = $task.TaskName
                Utilisateur = $task.Principal.UserId
            }
        }

        # Services
        $services = Invoke-Command -ComputerName $computer -ScriptBlock {
            Get-WmiObject Win32_Service | Where-Object {
                $_.StartName -eq "Administrateur" -or
                $_.StartName -eq "$($using:adminUser.SamAccountName)" -or
                $_.StartName -like "*\$($using:adminUser.SamAccountName)"
            } | Select-Object Name, StartName
        } -ErrorAction Stop

        foreach ($service in $services) {
            $tasksReport += [PSCustomObject]@{
                Computer    = $computer
                Type        = "Service"
                Nom         = $service.Name
                Utilisateur = $service.StartName
            }
        }
    }
    catch {
        Write-Warning "Erreur de connexion à $computer : $_"
    }
}

# Generate report
if ($tasksReport.Count -gt 0) {
    $reportPath = "$env:USERPROFILE\Desktop\AdminAccountAudit_$(Get-Date -Format 'yyyyMMdd').csv"
    $tasksReport | Export-Csv -Path $reportPath -NoTypeInformation -Delimiter ';'
    Write-Host "Rapport généré : $reportPath" -ForegroundColor Green
}
else {
    Write-Host "Aucune utilisation détectée." -ForegroundColor Yellow
}

$tasksReport | Format-Table -AutoSize                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           